import { View, Image, StyleSheet, ImageBackground, Text, ScrollView, TouchableOpacity, TextInput } from 'react-native'
import React, { useState, useRef } from 'react'
import { BlurView } from 'expo-blur';
import { Ionicons, Entypo, FontAwesome, AntDesign } from '@expo/vector-icons';
import RNDateTimePicker from '@react-native-community/datetimepicker';
import RBSheet from "react-native-raw-bottom-sheet";
import * as ImagePicker from 'expo-image-picker';
import Placesearch from 'react-native-placesearch';

export default function Index() {
  const locationRef = useRef();
  const [datePicker, setDatePicker] = useState(false)
  const [date, setDate] = useState("date");
  const datePickerFun = (event, text) => {
    setDate(text.toISOString().slice(0, 10))
    setDatePicker(false)
  }
  const [eventTitle, setEventTitle] = useState()
  const [location, setLocation] = useState()
  const [discription, setDiscription] = useState()
  const [photos, setPhotos] = useState([])
  const [files, setFiles] = useState([])



  const selectImages = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      allowsMultipleSelection: true,
      base64: false
    });

    if (!result.cancelled) {
      setPhotos(result.uri)
      let base64Img = `data:image/jpg;base64,${result.base64}`
      let apiUrl = 'https://api.cloudinary.com/v1_1/gigtune/image/upload';
      let data = {
        "file": base64Img,
        "upload_preset": "gigtune",
      }
      fetch(apiUrl, {
        body: JSON.stringify(data),
        headers: {
          'content-type': 'application/json'
        },
        method: 'POST',
      }).then(async r => {
        let data = await r.json()
        setPhotos(data.secure_url)
        return data.secure_url
      }).catch()
    }
  }




  const arr = [1, 2, 3, 4, 5, 6, 7]

  return (
    <View style={styles.container}>
      <ImageBackground source={require('../../../Assets/Images/bg.jpg')} resizeMode="cover" style={styles.background}>
        <ScrollView showsVerticalScrollIndicator={false}>
          <View style={styles.contentContainer}>
            <View style={styles.backIconContainer}>
              <Ionicons name="arrow-back-sharp" size={33} color="white" />
            </View>
            <View style={styles.titleGuideContainer}>
              <View>
                <Image source={require("../../../Assets/Images/arrow.png")} style={styles.titleGuideArrow} />
              </View>
              <View>
                <Text style={styles.titleText}>Give a catchy title to your event</Text>
              </View>
            </View>
            <BlurView intensity={40} tint="light" style={styles.inputTitleContainer}>
              <TextInput placeholder='Event Title' placeholderTextColor={"#fff"} style={styles.input} />
            </BlurView>
            <View>
              <TouchableOpacity style={styles.dataPickerBtn} onPress={() => setDatePicker(!datePicker)}>
                <Text style={styles.dataPickerBtnText}>{date == "date" ? "Pick a date" : date}</Text>
                <FontAwesome name="calendar" size={24} color="white" />
              </TouchableOpacity>
              {
                datePicker == true ?
                  <RNDateTimePicker value={new Date()} onChange={datePickerFun} />
                  : null
              }
              <TouchableOpacity style={styles.locationPickerBtn} onPress={() => locationRef.current.open()}>
                <View>
                  <Text style={styles.locationPickerBtnMainText}>Pick a location</Text>
                  <Text style={styles.locationPickerBtnSubText}>Address of location</Text>
                </View>
                <Ionicons name="location" size={30} color="white" />
              </TouchableOpacity>
              <BlurView intensity={40} tint="light" style={styles.TextArea}>
                <TextInput placeholder='Event description' placeholderTextColor={"#fff"} style={styles.TextAreaText} />
              </BlurView>
              <View>
                <Text style={styles.addPhotoTitle}>Add Photos</Text>
              </View>
              <BlurView intensity={40} tint="light" style={styles.photoPickContainer}>
                <View style={styles.userDetail}>
                  <Text style={styles.addFileSubTitle}>No Photo</Text>
                </View>
                <TouchableOpacity intensity={40} tint="light" style={styles.photoPickBtn} onPress={selectImages}>
                  <Entypo name="squared-plus" size={45} color="#fff" />
                </TouchableOpacity>
              </BlurView>
              <View>
                <Text style={styles.addFileTitle}>Add Files</Text>
              </View>
              <BlurView intensity={40} tint="light" style={styles.photoPickContainer}>
                <View style={styles.userDetail}>
                  <Text style={styles.addFileSubTitle}>No File</Text>
                </View>
                <TouchableOpacity intensity={40} tint="light" style={styles.photoPickBtn}>
                  <Entypo name="squared-plus" size={45} color="#fff" />
                </TouchableOpacity>
              </BlurView>
            </View>
            <View style={styles.btnContainer}>
              <TouchableOpacity style={styles.createEventBtn}>
                <Text style={styles.createEventBtnText}>Create Event/Save Changes</Text>
              </TouchableOpacity>
            </View>

            <RBSheet
              animationType={"slide"}
              closeOnDragDown={true}
              dragFromTopOnly={true}
              onOpen={(e) => { }}
              ref={locationRef}
              height={380}
              openDuration={250}
              closeDuration={250}
              customStyles={{
                wrapper: {
                  backgroundColor: "transparent"
                },
                draggableIcon: {
                  marginTop: -10
                },
                container: {
                  padding: 20
                }
              }}
            >
              <View>
                <View>
                  <Text style={styles.industriesBoxTitle}>Pick a location</Text>
                </View>
                <View style={styles.industriesSearchBox}>
                  <AntDesign name="search1" size={24} color="#797979" />
                  {/* <TextInput placeholder='Search location' style={styles.industriesSearchInput} placeholderTextColor="#797979" /> */}
                  {/* <GooglePlacesAutocomplete
                    placeholder='Search location'
                    enablePoweredByContainer={false}
                    fetchDetails={true}
                    onPress={(data, details) => console.log(data, details)}
                    returnKeyType={'search'}
                    query={{
                      key: 'AIzaSyBJTIYwTIrkgzHZ5s6sshI5uxks4by7D3g',
                      language: 'en',
                    }}
                    textInputProps={{
                      onChangeText: (text) => {
                        console.log(text);
                      }
                    }}
                  /> */}
                  <Placesearch
                    apikey={"AIzaSyBJTIYwTIrkgzHZ5s6sshI5uxks4by7D3g"} // required *
                    SelectedAddress={(data) => console.log(data)} // required *
                    // onClose={(data) => console.log(data)}
                    country="PK" //optional
                    coordinate={true} //optional
                    removeImg={true} //optional


                  />
                </View>
                <View style={styles.subIndustriesBox}>
                  <ScrollView horizontal={true} showsHorizontalScrollIndicator={false}>
                    {
                      arr.map((e, i) => {
                        return (
                          <TouchableOpacity key={i} style={styles.subIndustriesBoxItem}>
                            <Image source={require("../../../Assets/Images/dj.png")} style={styles.subIndustriesImage} />
                            <Text style={styles.subIndustriesTitle}>Home</Text>
                          </TouchableOpacity>
                        )
                      })
                    }
                  </ScrollView>
                </View>
                <TouchableOpacity style={styles.pickLocationBtn}>
                  <Text style={styles.pickLocationBtnText}>Pick from map</Text>
                </TouchableOpacity>
              </View>
            </RBSheet>
          </View>

        </ScrollView>
      </ImageBackground>
    </View >
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  background: {
    flex: 1,
    paddingHorizontal: 20
  },
  contentContainer: {
    paddingTop: 70
  },
  backIconContainer: {
    position: 'absolute',
    left: 0,
    top: 60
  },
  titleGuideContainer: {
    flexDirection: 'row',
    paddingHorizontal: 20,
    marginTop: 20
  },
  titleGuideArrow: {
    width: 110,
    height: 110
  },
  titleText: {
    color: "#EBE90B",
    marginTop: 20,
    fontSize: 16,
    marginLeft: -20
  },
  inputTitleContainer: {
    borderRadius: 10,
    height: 50,
    width: 320,
    justifyContent: 'center',
    alignItems: 'center',
    marginHorizontal: 12,
    marginTop: -40
  },
  input: {
    textAlign: 'center',
    fontSize: 17,
    fontWeight: '500',
    color: "white"
  },
  TextArea: {
    borderRadius: 10,
    height: 170,
    width: 320,
    justifyContent: 'flex-start',
    marginVertical: 14,
    padding: 10,
    marginHorizontal: 12,
  },
  TextAreaText: {
    fontSize: 17,
    fontWeight: '400',
    color: "white",
    opacity: 0.8
  },
  addPhotoTitle: {
    color: "white",
    fontSize: 16,
    fontWeight: "400",
    marginHorizontal: 12,
  },
  addFileTitle: {
    color: "white",
    fontSize: 16,
    fontWeight: "400",
    marginHorizontal: 12,
    marginTop: 10
  },
  photoPickContainer: {
    marginTop: 10,
    flexDirection: 'row',
    justifyContent: 'space-between',
    borderRadius: 10,
    marginHorizontal: 12,
    paddingLeft: 15
  },
  photoPickBtn: {
    padding: 10,
    borderRadius: 10
  },
  userDetail: {
    justifyContent: 'center',
  },
  addFileSubTitle: {
    color: "#fff",
    fontSize: 20,
    fontWeight: '600'
  },
  userPosition: {
    color: "#fff",
    fontSize: 15,
    fontWeight: '400'
  },
  btnContainer: {
    marginVertical: 30
